        OfferKnowMoreModel.appendTo("body");
        OfferKnowMoreModel = null;
    }
    $(window).resize(function() {
        if ($(window).width() + 15 < 768) {
            $('.BundleContent2').insertBefore($('.BundleContent1'));
        } else {
            $('.BundleContent1').insertBefore($('.BundleContent2'));
        }
    });
    if ($(window).width() + 15 < 768) {
        $('.BundleContent2').insertBefore($('.BundleContent1'));
    } else {
        $('.BundleContent1').insertBefore($('.BundleContent2'));
    }
    $('.IntroductionSlider').slick({
        dots: false,
        fade: true,
        infinite: true,
        initialSlide: 0,
