        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 3000,
        speed: 300,
    });
    $(".loanTypeUL").hide();
    $(".loanTypeUL").first().show();
    $(".HomePageLoneTypeUl li a span").first().addClass('lineWith');
    $(".HomePageLoneTypeUl li a").click(function(e) {
        e.preventDefault();
        $(".HomePageLoneTypeUl li a span").removeAttr('style');
        $(".HomePageLoneTypeUl li a span").removeClass('lineWith');
        $(".loanTypeUL").hide();
        var LID = $(this).attr("LID");
        $("#" + LID).show();
        $(this).children("p").css("color", "#ffffff");
        if ($(window).width() + 15 < 768) {
            $(this).children("span").css({
                "width": "100%"
            });
        } else {
            $(this).children("span").addClass("lineWith");
        }
    });
    $('.downArrowDiv a').click(function(e) {
        e.preventDefault();
        var hrefPage = $(this).attr('href');
        var AllPgid = $(this).attr('Tid');
        localStorage.setItem("AllPGID", AllPgid);
        window.location.href = hrefPage;
    });
