            }
            $('.searchOpen').show();
        }, 500);
    });
});

$(document).ready(function() {
    $("body").ready(function() {
        getSaveRoi();
    });
    /// To get data on change of Bundle to get Save ROI
    var getSaveRoi = function() {
        let request = {
            loanAmountenterByCustomer: $("#loanAmtInput").val().replace(/\,/g, '') || '',
            product: $("#Loan option:selected").val(),
            insuranceCode: $("#Insurance option:selected") && $("#Insurance option:selected").val() || '',
            vasCode: $("#VAS option:selected") && $("#VAS option:selected").val() || ''
        };
        console.log('request--->', request);
        // request = {
        //   "loanAmountenterByCustomer": 80000,
        //   "product": "SLTOL",
        //   "insuranceCode": "GCPP",
        //   "vasCode": "FFR"
        // };
        httpInterceptor.Post(
			"https://apis.qa.bfsgodirect.com/v1/bundles/savings",
            request,
            null,
            function(data) {
                let productInfo = data.payload.productBundleOutput
                $('#saveRateofIntrest').text(productInfo.saveRoi+'%');
                $('#saveAmount').text(productInfo.saveAmount);
                console.debug(data);
            },
            function(error) {
                console.debug(error);
            });
    };

	$("#calculate_savings").click(".BundlSelectOpt span, .BundlerRightBox li .minus, .BundlerRightBox li .plus", function(
        e) {
        getSaveRoi();
    });

});

