jQuery(function($){

  // Marketo Contact Form Selection
  $('.marketo-form-select').change(function(){
    $('.marketo-form').hide();
    $('.marketo-form[data-form-name="'+ $(this).val() +'"]').show();
  });

  $(window).load(function(){
    $('.mktoFormRow .mktoFieldWrap label[for="phone"]')
      .parents('.mktoFieldDescriptor')
      .siblings('.mktoFormCol')
      .css({
        top: '-40px',
        left: '510px'
      })
      .find('.mktoOffset')
      .css('display','none')

    var form = getUrlParameter('f')
    console.log(form);
    if(form != ''){
      $('.marketo-form-select').val(form).trigger('change')
    }
  });
});

window.addClassToCheckbox = function(el){
  jQuery(el)
    .find('input.mktoField:checkbox')
    .parents('.mktoFormRow')
    .addClass('marketo-checkbox')
}

// Thanks to http://stackoverflow.com/questions/19491336/get-url-parameter-jquery-or-how-to-get-query-string-values-in-js
var getUrlParameter = function getUrlParameter(sParam) {
    var sPageURL = decodeURIComponent(window.location.search.substring(1)),
        sURLVariables = sPageURL.split('&'),
        sParameterName,
        i;

    for (i = 0; i < sURLVariables.length; i++) {
        sParameterName = sURLVariables[i].split('=');

        if (sParameterName[0] === sParam) {
            return sParameterName[1] === undefined ? true : sParameterName[1];
        }
    }
};
