jQuery(function($){

  /* START - Cookie policy banner */
  if( $.cookie('cookie_seen') === undefined){
    $('body').prepend("<div class='cookie-banner'><a class='close' id='close-cookie-banner' href='javascript:void(0)'>&times;</a><h6>Cookie Privacy Alert</h6>This website uses cookies to improve its functionality and performance. By continuing to browse the site you are accepting our use of cookies. To find out more about the cookies we use, please read our <a href='/policies/cookies'>Cookie Policy</a></div>");
  }

  $('a#close-cookie-banner').on('click', function(){ hide_cookie_banner(); });

  $(window).load(function(){
    resize_slider();
    window.setTimeout(hide_cookie_banner,60000);
  });

  function hide_cookie_banner(){
    $('.cookie-banner').fadeOut().remove();
    $.cookie("cookie_seen", true, { expires: 30, path: '/' });
  }

  /* END - Cookie policy banner */

  /* START - Cookies from salesforce params */
  var urlParams;
  (window.onpopstate = function () {
    var match,
        pl     = /\+/g,  // Regex for replacing addition symbol with a space
        search = /([^&=]+)=?([^&]*)/g,
        decode = function (s) { return decodeURIComponent(s.replace(pl, " ")); },
        query  = window.location.search.substring(1);

    urlParams = {};
    while (match = search.exec(query))
       urlParams[decode(match[1])] = decode(match[2]);
  })();


  var params = [
    'Online_Source_ID__c',
    'Asset_ID__c',
    'Nurture_ID__c',
    'Product_ID__c'
  ];
  for ( i in params){
    p = params[i];
    if( p in urlParams && $.cookie('tata_comms_'+p) === undefined ){
      $.cookie('tata_comms_'+p, urlParams[p], { expires : 3650 });
    }
  }

  /* END */

  // Resizing
  $(window).bind("resize", function(){
    resize_slider();
  });

  function resize_slider(){
    var windowWidth = $(window).width();
    var windowHeight = $(window).height();

    var width_divs = '.slider-window,.main-content,.main,.header__content,header,.main-content__footer,.unslider,.panels-flexible-region-1-front_page___thumnails';
    var mobile_divs = '.main-content,.main,.header__content,.region-header-nav,header,.main-content__footer,.unslider';
    var height_divs = '.slider-window,li.slide';
    var mobile = false;

    var new_height = windowHeight - 180;
    var new_width = windowWidth;

    if (new_width < 728) { new_width = "100%"; mobile = true; }

    if(new_height < 672){ new_height = 672; }

    if(mobile){
      $(mobile_divs).width(new_width);
      $('.slider-window').width('100%');
      $('body').addClass('mobile-nav');
    }else{
      $(width_divs).width(new_width);
      $('body').removeClass('mobile-nav');
    }


    if($('.slide:first img.slide__image').height() < new_height ){
      $(height_divs).height($('.slide:first img.slide__image').height());
    }else{
      $(height_divs).height(new_height);
    }

  }
  // about maximise/minimise
  $('.view-team-member-list .toggle-open').click( function(event) {
    event.preventDefault();
    $container = $(this).parents('.views-row');
    $container.children('.team-member__summary').hide();
    $container.children('.team-member-body').slideDown();
  });
  $('.view-team-member-list .toggle-close').click( function(event) {
    event.preventDefault();
    $container = $(this).parents('.views-row');
    $container.children('.team-member__summary').show();

    var offset = $container.offset().top - 150;
    $('body').animate({ scrollTop: offset}, 400, function(){
      $container.children('.team-member-body').slideUp()
    });
  });

  var menuOffset = $('#main-navigation')[0].offsetTop;
  var submenuOffset = $('#sub-navigation')[0].offsetTop - $('#main-navigation').height()*2;
  $(document).bind('ready scroll',function() {
    var docScroll = $(document).scrollTop();
    if(!$('body').hasClass('mobile-nav') ){ // Dont fix menu to top for mobile menu
      if(docScroll >= menuOffset) {
        $('#main-navigation').addClass('fixed').css("top", 0);
      } else {
        $('#main-navigation').removeClass('fixed');
      }
      if (docScroll >= submenuOffset) {
        $('#sub-navigation').addClass('fixed').css("top", 0 + $('#main-navigation').height());
      } else {
        $('#sub-navigation').removeClass('fixed').css("top", 0);
      }
    }
  });

  $('#site-sidelinks .email, #site-sidelinks .share').hover(function() {
    $(this).children('.slide-out').fadeToggle();
  });

  $('#site-map').masonry({
    itemSelector: '.site-map-box'
  });


  // Menu animatoin
  $('#main-navigation ul.menu > li, #sub-navigation li').hover(function(){
    if( !$('body').hasClass('mobile-nav') ){
      $(this).find('> .dropdown-menu').fadeToggle(100)
    }
  })


  $('.block-search .search-form .custom-search-box')
    .focus(function(){
      $('.block-search').css({
        position: 'absolute',
        left: $(this).parents('.block-search').position().left
      }).animate({
        width: '340px'
      })
    })
    .blur(function(){
      $('.block-search').animate({
        width: '200px'
      }, function() {
        $('.block-search').css({
        position: 'static',
          left: 0
        })
      })
    })

});
;
jQuery(function($){

  $('.mobile-menu').click(function(){
    console.log('foo')
    if ($('#main-navigation').hasClass('open')){
      $('#main-navigation').removeClass('open');

    }else{
      $('#main-navigation').addClass('open');
    }
  });


  $('#main-navigation ul.menu li a.dropdown-toggle').click(function(evnt){
    if ( $('body').hasClass('mobile-nav') ){
      if ($(this).hasClass('open')){
        $(this).removeClass('open');
        $(this).siblings('ul.dropdown-menu').hide();
      }else{
        $(this).addClass('open');
        $(this).siblings('ul.dropdown-menu').show();
      }
      evnt.stopPropagation();
      evnt.preventDefault();
      return false;
    }
  });

});
;
